CREATE VIEW latest_results_v AS SELECT t1.period_date, t1.mean_forecast, t2.vendor_key, t2.indicator_short_info, t3.forecast_type, t1.run_id, t6.presentation_unit, t5.timestamp from forecast_data t1 LEFT JOIN (indicators t2) ON (t2.indicator_id = t1.indicator_id) LEFT JOIN (forecast_types t3) ON (t3.forecast_type_id = t1.forecast_type_id) LEFT JOIN (run_table t4) ON (t4.run_id > t1.run_id) LEFT JOIN (run_table t5) ON (t5.run_id = t1.run_id) LEFT JOIN (presentation_units t6) ON (t2.indicator_presentation = t6.unit_id) where t4.timestamp is NULL;